import React, {Component} from 'react';
import ReactDOM from 'react-dom'
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import Newsapi from './Newsapi';
import './api.png';
class App extends Component {



  state = {
    posts: []

}



componentDidMount() {

    axios.get('https://newsapi.org/v2/everything?q=tesla&from=2021-04-11&sortBy=publishedAt&apiKey=6109346afd364c538a4b723e0adc77a4')
      .then( response => {
       const SlicePosts = response.data.articles.slice(0, 10);
       const updatedPosts = SlicePosts.map(post => {

         return {
           ...SlicePosts,
           curTime: new Date().toLocaleString()
         }
       })
        this.setState({posts: updatedPosts});
      console.log(response.data.articles);
      });

  }
  render () {

    const posts = Array.isArray(this.state.posts) && this.state.posts.map( (post, index) => {
       return <Newsapi id={index} key={index} author={post.author} source={post.source.name} image='api.png' url={post.url} title={post.title} date={post.curTime} />
    });
    return (
    <div>
    <table className="table table-dark">
     <thead>
    <tr>
      <th scope="col">Id</th>
      <th scope="col">Image</th>
      <th scope="col">Source</th>
      <th scope="col">Author</th>
      <th scope="col">title</th>
      <th scope="col">Date</th>
      <th scope="col">URL</th>
    </tr>
  </thead>
  <tbody>
   {posts}
   </tbody>
   </table>
   <nav>
  <ul className="pagination">
    <li className="page-item"><a className="page-link" >Previous</a></li>
    <li className="page-item"><a className="page-link" >1</a></li>
    <li className="page-item"><a className="page-link" >2</a></li>
    <li className="page-item"><a className="page-link" >..</a></li>
    <li className="page-item"><a className="page-link" >10</a></li>
  </ul>
</nav>
    </div>
    );
  }
}



export default App;
