import React, {Component} from 'react';
import ReactDOM from 'react-dom'
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import Newsapi from './Newsapi';
class App extends Component {

  state = {
    posts: []
}



componentDidMount() {

    axios.get('https://newsapi.org/v2/everything?q=tesla&from=2021-04-11&sortBy=publishedAt&apiKey=6109346afd364c538a4b723e0adc77a4')
      .then( response => {

        this.setState({posts: response.data});
      // console.log(response.data);
      });

  }
  render () {

    const posts = Array.isArray(this.state.po) && this.state.posts.map( post => {
       return <Newsapi title={posts.title} />
    });
    return (
    <div>
   {posts}
    </div>
    );
  }
}



export default App;
