function Pagination({ postsPerPage, totalPosts }) {
    console.log('total', totalPosts)

    const pageNumbers = [];

    for (let i = 1; i <= Math.ceil(totalPosts / postsPerPage); i++) {
        pageNumbers.push(i);
        
    }
console.log('vhjvjvhjv', pageNumbers)

    return (
        <>
            <nav>
                <ul className="pagination">
                    {pageNumbers.map(number => {
                        <li key={number} className="page-item"><a className="page-link" >
                            {number}
                        </a></li>
                    })
                    }

                    {/* <li className="page-item"><a className="page-link" >10</a></li> */}
                </ul>
            </nav>
        </>
    )
}

export default Pagination
